package com.nic.in.model;

public class General 
{
	private String petition_id;
	private String petitioner_id;
	private String userid;
	private String appeal;
	private String pet_detail;
	private String entry_date;
	private String action_date;
	private String action_userid;
	public General() {
		super();
		// TODO Auto-generated constructor stub
	}
	public String getPetition_id() {
		return petition_id;
	}
	public void setPetition_id(String petition_id) {
		this.petition_id = petition_id;
	}
	public String getPetitioner_id() {
		return petitioner_id;
	}
	public void setPetitioner_id(String petitioner_id) {
		this.petitioner_id = petitioner_id;
	}
	public String getUserid() {
		return userid;
	}
	public void setUserid(String userid) {
		this.userid = userid;
	}
	public String getAppeal() {
		return appeal;
	}
	public void setAppeal(String appeal) {
		this.appeal = appeal;
	}
	public String getPet_detail() {
		return pet_detail;
	}
	public void setPet_detail(String pet_detail) {
		this.pet_detail = pet_detail;
	}
	public String getEntry_date() {
		return entry_date;
	}
	public void setEntry_date(String entry_date) {
		this.entry_date = entry_date;
	}
	public String getAction_date() {
		return action_date;
	}
	public void setAction_date(String action_date) {
		this.action_date = action_date;
	}
	public String getAction_userid() {
		return action_userid;
	}
	public void setAction_userid(String action_userid) {
		this.action_userid = action_userid;
	}
	
	
}
